/**
 * Socket Server Slice
 */
public class SocketServerTest {
    private static final String TAG = SocketServerTest.class.getSimpleName();

    private static final HiLogLabel LABEL_LOG = new HiLogLabel(3, 0xD000F00, TAG);

    private static final int UDP_PORT = 8899;
    private static final int TCP_PORT = 9988;
    private InetAddress ipaddress = null;

    private String base64Img = "data:image/jpeg;base64,";

    private String tearus = "data:image/jpeg;base64,";



    public SocketServerTest(Context context) {
        try {
            ipaddress = InetAddress.getByName(this.getLocationIpAddress(context));
        } catch (UnknownHostException e) {
            e.printStackTrace();
        }
    }

    private String getLocationIpAddress(Context context) {
        WifiDevice wifiDevice = WifiDevice.getInstance(context);
        Optional<WifiLinkedInfo> linkedInfo = wifiDevice.getLinkedInfo();
        int ip = linkedInfo.get().getIpAddress();
        return (ip & 0xFF) + "." + ((ip >> 8) & 0xFF) + "." + ((ip >> 16) & 0xFF) + "." + (ip >> 24 & 0xFF);
    }

    public void udpServer() {
        ThreadPoolUtil.submit(()->{
            try (DatagramSocket socket = new DatagramSocket(UDP_PORT)) {
                DatagramPacket packet = new DatagramPacket(new byte[255], 255);
                socket.setBroadcast(true);
                System.out.println("UDP服务器: 服务器启动了....");
                while (true) { // 监听
                    socket.receive(packet);
                    System.out.println("UDP服务器: 收到了！");

                    // 收到了数据,准备回应
                    DatagramSocket Rsocket = null;
                    try {
                        Rsocket = new DatagramSocket(); // 重新创建一个套接字回应客户端
                        ZSONObject json = new ZSONObject();
                        json.put("GESTURE_SERVER", ipaddress.getHostAddress());
                        json.put("GESTURE_SERVER_PORT", UDP_PORT);
                        byte[] Sbuffer = json.toString().getBytes();    // send buffer
                        InetAddress ClientAdd = packet.getAddress();    // client address
                        int ClientPost = packet.getPort();
                        DatagramPacket sendPacket = new DatagramPacket(Sbuffer, Sbuffer.length,
                                ClientAdd, ClientPost);  // send to client

                        System.out.println("UDP服务器: 客户的IP地址" + packet.getAddress().getHostAddress() +
                                " 客户的端口号" + packet.getPort());
                        Rsocket.send(sendPacket);   // 将数据包回给客户端
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                    packet.setLength(255);
                    Thread.sleep(1000);
                }
            } catch (IOException | InterruptedException e) {
                HiLog.error(LABEL_LOG, "%{public}s", "StartServer  IOException | InterruptedException");
            }
        });
    }

    public void tcpServer() {
        ThreadPoolUtil.submit(()->{
            System.out.println("TCP服务器: 服务器 Tcp 服务启动了....");
            try {
                ServerSocket socket = new ServerSocket();
                InetSocketAddress address = new InetSocketAddress(ipaddress, TCP_PORT);
                socket.bind(address);


                // 服务器发送端
                while (true) {
                    Socket client = socket.accept();    // 监听

                    // 服务器的接收端
                    ThreadPoolUtil.submit(()->{

                        final String GESTURE_MSG_HEAD = "gesture_msg_head"; // 头部标识
                        final int GESTURE_HEAD_FLAG_LENGTH = GESTURE_MSG_HEAD.length();  // 头部标识长度
                        final int GESTURE_HEAD_LENGTH = GESTURE_HEAD_FLAG_LENGTH + 4;    // 头部长度
                        try {
                            InputStream in = client.getInputStream();

                            int bodyLength = 0;
                            byte[] headBuf = new byte[GESTURE_HEAD_LENGTH];
                            byte[] tmp = new byte[0];
                            int counter = 0;        // 计数器
                            int need = GESTURE_HEAD_LENGTH;  // 还需要的字节数
                            while (client.isConnected()) {
                                // 接收数据包并解析
                                while ((counter = in.read(headBuf)) > 0) {
                                    // 提取出头部 = body length + 标志
                                    System.out.println(
                                            "TCP服务器: " + "读取到了head数据包....长度 " + counter + " need: " + need);
                                    if (counter < need) {
                                        // 头部还没读完
                                        tmp = mergeByte(tmp, headBuf, 0, counter);
                                        need = GESTURE_HEAD_LENGTH - tmp.length;
                                        headBuf = new byte[need];    // 重新指定接受缓冲区大小
                                        continue;
                                    } else {
                                        // 1. 前面出现断包,此时已经接受了全部的包,合包操作
                                        // 2. 前面没有出现断包,直接把headBuf给自己
                                        headBuf = mergeByte(tmp, headBuf, 0, counter);
                                        need = GESTURE_HEAD_LENGTH - headBuf.length;
                                    }
                                    // 头部读完了,开始解析头部
                                    // +--------------------+
                                    // |  gesture_msg_head  |
                                    // +--------------------+
                                    // | bodyLength = 4byte |
                                    // +--------------------+
                                    System.out.println("TCP服务器: " + "开始解析头部...");
                                    byte[] gesture_msg_head_bytes = splitBytes(headBuf, 0, GESTURE_HEAD_FLAG_LENGTH);
                                    String gesture_msg_head = new String(gesture_msg_head_bytes);   // 获取头部标签
                                    // 数据包body长度
                                    bodyLength = byteArrayToInt(splitBytes(headBuf, GESTURE_HEAD_FLAG_LENGTH, headBuf.length));
                                    if (!gesture_msg_head.equals(GESTURE_MSG_HEAD)) { // 头部标识不对!
                                        System.out.println("TCP服务器: " + "接受到错误的包...");
                                        break;
                                    }
                                    System.out.println("TCP服务器: " + "头部解析完毕...头部标签内容为'" + gesture_msg_head + "' " +
                                            "头部包含的数据包body的长度为 " + bodyLength);


                                    // 开始读取body
                                    byte[] bodyBuf = new byte[bodyLength];
                                    tmp = new byte[0];  // 重置tmp
                                    // 清零count计数器
                                    need = bodyLength;  // 还需要读取的字节数
                                    while (counter < need) {
                                        while ((counter = in.read(bodyBuf)) > 0) { // 又读一次!!!
                                            System.out.println(
                                                    "TCP服务器: " + "读取到了body数据包....长度 " + counter + " need: " + need);
                                            if (counter < need) {
                                                // body还没读完
                                                tmp = mergeByte(tmp, bodyBuf, 0, counter);
                                                need = bodyLength - tmp.length;
                                                bodyBuf = new byte[need];    // 重新指定接受缓冲区大小
                                            } else {
                                                // 1. 前面出现断包,此时已经接受了全部的包,合包操作
                                                // 2. 前面没有出现断包,直接把headBuf给自己
                                                bodyBuf = mergeByte(tmp, bodyBuf, 0, bodyBuf.length);
                                                need = bodyLength - bodyBuf.length;
                                                break;
                                            }
                                        }
                                    }

                                    //开始解析body
                                    System.out.println("TCP服务器: " + "开始解析body...");
                                    ZSONObject jsonBody = ZSONObject.stringToZSON(new String(bodyBuf));
                                    System.out.println("TCP服务器: " + "body数据包解析完毕...");
                                    System.out.println("TCP服务器: jsonBody..." + jsonBody.toString());
                                    jsonBody.clear();
                                    // 传给FA

                                    // 重新初始化，接收下一个TCP包
                                    bodyLength = 0;
                                    headBuf = new byte[GESTURE_HEAD_LENGTH];
                                    tmp = new byte[0];
                                    counter = 0;        // 计数器
                                    need = GESTURE_HEAD_LENGTH;  // 还需要的字节数
                                    break;  // 结束本TCP包,接收下一个TCP包
                                }

                            }
                        } catch (IOException e) {
                            e.printStackTrace();
                        }
                    });

                    // 服务器发送端
                    System.out.println("TCP服务器: 收到了...");
                    InetSocketAddress clientArr = (InetSocketAddress)client.getRemoteSocketAddress();
                    System.out.println("TCP服务器: 客户端地址tcp地址 " + clientArr.getHostName());
                    ZSONObject zson = new ZSONObject();
                    zson.put("image", tearus);
                    zson.put("width", 416);
                    zson.put("height", 416);

                    ZSONObject Inzson = new ZSONObject();
                    Inzson.put("class", 8);
                    Inzson.put("name", "thump_up");
                    Inzson.put("x", 63);
                    Inzson.put("y", 69);
                    Inzson.put("w", 156);
                    Inzson.put("h", 258);
                    Inzson.put("Confidence", 0.669677734375);
                    ZSONArray zsonArray = new ZSONArray();
                    zsonArray.add(Inzson);
                    zson.put("gesture", zsonArray); // 数组

                    ZSONObject res = new ZSONObject();
                    res.put("board2phone", zson);
                    System.out.println(res.toString());
                    while (client.isConnected()) {
                        // 开始服务
                        for (int i = 0; i < 1; ++i) {
                            OutputStream outTcp = client.getOutputStream();
//                            client.setSendBufferSize(1024);
                            String str = res.toString();
//                            System.out.println("TCP服务器: 开始发数据了.." + " str长度" + "gesture_msg_head".length());
                            byte[] send = mergeByte(("gesture_msg_head").getBytes(StandardCharsets.US_ASCII),
                                    intToByteArray(str.length()), 0, intToByteArray(str.length()).length);
                            send = mergeByte(send, str.getBytes(), 0, str.getBytes().length);
                            outTcp.write(send);
                        }
                        Thread.sleep(5000);
                    }
                    client.close();
                }

            } catch (IOException | InterruptedException e) {
                e.printStackTrace();
            }
        });
    }

    private byte[] splitBytes(byte[] bytes, int begin, int end) {
        byte[] ret = new byte[end - begin];
        int j = 0;
        for (int i = begin; i < end; ++i) {
            ret[j++] = bytes[i];
        }
        return ret;
    }

    private int byteArrayToInt(byte[] bytes) {
        return   bytes[3] & 0xFF |
                (bytes[2] & 0xFF) << 8 |
                (bytes[1] & 0xFF) << 16 |
                (bytes[0] & 0xFF) << 24;
    }




    private byte[] mergeByte(byte[] a, byte[] b, int begin, int end) {
        byte[] add = new byte[a.length + end - begin];
        int i = 0;
        for (i = 0; i < a.length; i++) {
            add[i] = a[i];
        }
        for (int k = begin; k < end; k++, i++) {
            add[i] = b[k];
        }
        return add;
    }


    private byte[] intToByteArray(int i) {
        byte[] result = new byte[4];
        result[0] = (byte)((i >> 24) & 0xFF);
        result[1] = (byte)((i >> 16) & 0xFF);
        result[2] = (byte)((i >> 8) & 0xFF);
        result[3] = (byte)(i & 0xFF);
        return result;
    }

}

